tasks.task:
docker pull pegi3s/two-way-blast:${version_two_way_blast}
docker pull pegi3s/emboss:${version_emboss}
docker pull pegi3s/utilities:${version_utilities}
docker pull pegi3s/seqkit:${version_seqkit}
docker pull pegi3s/blast_utilities:${version_blast_utilities}
docker pull pegi3s/pss-fs:${version_pss_fs}

tasks.foreach:
FILENAME=$(basename -- "${protein_ref}") HEADER=$(head -1 ${protein_ref}) NAME=$(echo "$HEADER" | sed 's/>//g' | sed "s/_1$//g") echo -e "${protein_ref} \t $FILENAME \t $NAME \t ${working_dir}/${temporary_dir}/orthologs/${NAME}.orthologs" echo ${HEADER}
if [ -f "${working_dir}/${temporary_dir}/orthologs/${NAME}.orthologs" ]; then docker run --rm \ -v ${host_working_dir}:/data \ pegi3s/blast_utilities:${version_blast_utilities} m3f1 \ /data/${temporary_dir}/orthologs/${NAME}.orthologs \ /data/${temporary_dir}/translated_ref.split/${FILENAME} \ /data/${temporary_dir}/orthologs/${NAME}.result
echo "${NAME}" >> ${working_dir}/${temporary_dir}/translated_ref_done else echo "${NAME} does not have an orthologs file" echo "${NAME}" >> ${working_dir}/${temporary_dir}/translated_ref_without_orthologs_file fi

mkdir -p ${working_dir}/${temporary_dir}/orthologs_clean
FILENAME=$(basename -- "${orthologs_file}")
docker run --rm \ -v /tmp:/tmp \ -v /var/run/docker.sock:/var/run/docker.sock \ -v ${host_working_dir}:${host_working_dir} \ --env VERSION_SEQKIT=${version_seqkit} \ --env VERSION_PEGI3S_UTILITIES=${version_utilities} \ --env VERSION_EMBOSS=${version_emboss} \ pegi3s/utilities:${version_utilities} fasta_remove_sequences_with_in_frame_stops_or_n \ ${host_working_dir}/${temporary_dir}/orthologs/${FILENAME} \ ${host_working_dir}/${temporary_dir}/orthologs_clean/${FILENAME}

tasks.task:
rm -rf ${working_dir}/${temporary_dir}/orthologs_ref_species
INPUT_GLOBAL_DIR=${working_dir}/${temporary_dir}/orthologs_ref_species/input_global mkdir -p ${INPUT_GLOBAL_DIR} cd ${INPUT_GLOBAL_DIR}
cp ${working_dir}/${input_data_dir}/${reference_file} ./ cp ${working_dir}/${global_reference_file} ./
sed -i "s/.>/_/g" * sed -i "s/[^a-zA-Z0-9>]/_/g" *
while grep "__" ${INPUT_GLOBAL_DIR}/* > ${working_dir}/${temporary_dir}/orthologs_ref_species/dum do sed -i "s/__/_/g" * done
docker run --rm \ -v /tmp:/tmp \ -v /var/run/docker.sock:/var/run/docker.sock \ -v ${host_working_dir}/${temporary_dir}/orthologs_ref_species/input_global:/input \ -v ${host_working_dir}/${temporary_dir}/orthologs_ref_species/orthologs:/output \ pegi3s/two-way-blast:${version_two_way_blast} \ --num-tasks ${compi_num_tasks} -- \ --reference_file ${reference_file} \ --blast_type ${blast_type}
